import flet as ft


def main(page: ft.Page):
    page.add(
        ft.Image(
            src="https://picsum.photos/320/200?random=2",
            width=360,
            height=220,
            fit=ft.BoxFit.COVER,
            # blue image
            placeholder_src="data:image/png;base64,",  # noqa: E501
            fade_in_duration=400,
            fade_out_duration=200,
        ),
    )


if __name__ == "__main__":
    ft.run(main)
