import pytest

import flet as ft
import flet.testing as ftt


@pytest.mark.asyncio(loop_scope="module")
async def test_basic(flet_app: ftt.FletTestApp, request):
    await flet_app.assert_control_screenshot(
        request.node.name,
        ft.CupertinoTextField(
            placeholder_text="Cupertino text field",
            placeholder_style=ft.TextStyle(color=ft.Colors.GREY_400),
            margin=25,
        ),
    )


@pytest.mark.asyncio(loop_scope="module")
async def test_filled(flet_app: ftt.FletTestApp, request):
    await flet_app.assert_control_screenshot(
        request.node.name,
        ft.CupertinoTextField(
            placeholder_text="Cupertino text field",
            placeholder_style=ft.TextStyle(color=ft.Colors.GREY_400),
            value="Testing Textfield 1,2,3!",
            margin=25,
        ),
    )


@pytest.mark.asyncio(loop_scope="module")
async def test_label(flet_app: ftt.FletTestApp, request):
    await flet_app.assert_control_screenshot(
        request.node.name,
        ft.CupertinoTextField(
            label="Textfield Label",
            label_style=ft.TextStyle(italic=True, weight=ft.FontWeight.BOLD),
            margin=25,
        ),
    )


@pytest.mark.asyncio(loop_scope="module")
async def test_label_and_image(flet_app: ftt.FletTestApp, request):
    await flet_app.assert_control_screenshot(
        request.node.name,
        ft.CupertinoTextField(
            label="Textfield Label",
            label_style=ft.TextStyle(italic=True, weight=ft.FontWeight.BOLD),
            bgcolor=ft.Colors.BLUE_GREY,
            image=ft.DecorationImage(
                src_base64=""
            ),
        ),
        pump_duration=ft.Duration.from_unit(seconds=4),
        pump_times=1,
    )
